mapper.select:
SELECT COUNT(*)
FROM User
WHERE status_id = 'U01';

SELECT COUNT(*)
FROM User
WHERE user_is_host = 'Y';

SELECT COUNT(*)
FROM InquiryChattingRoom
WHERE status_id = 'I01';

mapper.update:
update InquiryChattingRoom
set status_id = 'I02', admin_id = #{admin_id}
where chatting_room_id = #{chatting_room_id}

UPDATE Room
SET status_id = 'RA02'
WHERE room_id = #{room_id};

UPDATE Room
SET status_id = 'RA03'
WHERE room_id = #{room_id};

mapper.select:
SELECT *
FROM Notice
ORDER BY created_at DESC LIMIT 20;

SELECT *
FROM InquiryChattingRoom
ORDER BY created_at DESC LIMIT 20;

SELECT *
FROM Coupon
ORDER BY created_at DESC LIMIT 20;
