mapper.select:
SELECT COUNT(*)
FROM User
WHERE status_id = 'U01';

SELECT COUNT(*)
FROM User
WHERE user_is_host = 'Y';

SELECT COUNT(*)
FROM Inquiry
WHERE inquiry_is_answered = 'N';

mapper.update:
UPDATE Room
SET status_id = 'RA02'
WHERE room_id = #{room_id};

UPDATE Room
SET status_id = 'RA03'
WHERE room_id = #{room_id};

mapper.select:
SELECT *
FROM Notice
ORDER BY created_at DESC LIMIT 20;

SELECT *
FROM Inquiry
ORDER BY created_at DESC LIMIT 20;

SELECT *
FROM Coupon
ORDER BY created_at DESC LIMIT 20;
