mapper.select:
select u.user_email, u.user_nickname, r.user_id, r.room_id, r.room_address, r.room_address_detail, r.status_id room_status_id, r.room_main_photo, b.booking_id , b.status_id booking_status_id, b.booking_checkin_date, b.booking_checkout_date, b.booking_checkin_time, b.booking_checkout_time from Room r, Booking b, User u
where r.room_id = b.room_id and u.user_id = b.user_id and u.user_email = #{user_email} and b.status_id = 'B05';
where r.room_id = b.room_id and u.user_id = b.user_id and u.user_email = #{user_email} and b.status_id != 'B05';
