mapper.select:
select u.user_email, u.user_nickname, r.user_id, r.room_id, r.room_address, r.room_address_detail, r.status_id, r.room_status_id, r.room_main_photo, r.room_weekday_price, r.room_weekend_price, b.booking_id, b.status_id booking_status_id, b.booking_checkin_date, b.booking_checkout_date, b.booking_checkin_time, b.booking_checkout_time, b.booking_total_pay_amount, b.is_user_review, b.created_at, p.payment_id, p.booking_id, p.payment_is_success, p.payment_paid_amount, p.payment_status, p.payment_pg_provider, p.payment_is_approved from Room r join Booking b on r.room_id = b.room_id join User u on u.user_id = b.user_id left join Payment p on b.booking_id = p.booking_id where u.user_email = #{user_email}
and b.status_id = 'B05'
and b.status_id != 'B05'
ORDER BY b.created_at desc;

mapper.update:
UPDATE Booking
SET is_user_review = 'Y'
Where booking_id = #{booking_id}

UPDATE Booking b LEFT JOIN Payment p On b.booking_id = p.booking_id SET b.status_id = 'B05'
WHERE b.booking_checkout_date < CURDATE()
AND b.status_id = 'B02' AND p.payment_is_approved = 'Y';
